import React from 'react';
import Fade from 'react-reveal/Fade';
import FadeIn from 'react-fade-in/lib/FadeIn';

export default function HeroSection() {
    return (
        <header>
            <FadeIn>
                <div
                    className="p-5 text-center bg-image"
                    style={{
                        width: '33%', // Set the desired width of the hero section
                        margin: '0 auto', // Center the hero section horizontally
                        height: '50%',
                    }}
                >
                    <div className="mask" style={{ backgroundColor: '' }}>

                        <div className="d-flex justify-content-center align-items-center h-100">
                            <div className="text-center pb-12 md:pb-16">
                                <h1 className="text-black">Jesus Celestino,</h1>
                                <h2 className="text-black">Aspiring Software Developer</h2>
                                <p className="text-black">
                                    Recent graduate in computer science, passionate about
                                    launching my career as a software engineer. I am thrilled
                                    to showcase my skills, projects, and eagerness to
                                    contribute to a dynamic and innovative team.
                                </p>
                            </div>
                        </div>

                    </div>
                </div>
            </FadeIn>
        </header>
    );
}
