export default function HeroSection() {
    return (
      <header>
        <div
          className="p-5 text-center d-flex justify-content-center align-items-center"
          style={{
            width: '100%',
            margin: '5 auto',
            height: '50vh', // Adjust the height to fill the viewport
            position: 'relative', // Add position relative for the parent div
          }}
        >
          <div
            className="mask"
            style={{
              position: 'absolute',
              top: 0,
              left: 0,
              width: '100%',
              height: '100%',
              background: 'linear-gradient(to bottom, rgba(0, 0, 0, 0.5) 0%, rgba(0, 0, 0, 0.5) 100%)',
              zIndex: -1, // Set a negative z-index to place it behind the content
            }}
          ></div>
  
          <div>
            <h1 className="text-white">Jesus Celestino,</h1>
            <h2 className="text-white">Aspiring Software Developer</h2>
            <p className="text-white">
              Recent graduate in computer science, passionate about launching my career as a software engineer. I am thrilled to showcase my skills, projects, and eagerness to contribute to a dynamic and innovative team.
            </p>
          </div>
        </div>
      </header>
    );
  }
